DROP TABLE IF EXISTS `topic`;
CREATE TABLE `topic` (
  `tid`            BIGINT(20)  NOT NULL AUTO_INCREMENT
  COMMENT '帖子自增id',
  `tsid`           INT(11)     NOT NULL
  COMMENT '所属板块',
  `tuid`           BIGINT(20)  NOT NULL
  COMMENT '发帖人',
