  COMMENT '主题',
  `tcontent`       LONGTEXT    NOT NULL
  COMMENT '内容',
  `ttime`          TIMESTAMP   NOT NULL DEFAULT CURRENT_TIMESTAMP
  COMMENT '发布时间',
  `treplaycount`   BIGINT(20)           DEFAULT '0'
  COMMENT '回帖量',
  `tclickcount`    BIGINT(20)           DEFAULT '0'
  COMMENT '点击量',
  `tlastclicktime` DATETIME             DEFAULT NULL
  COMMENT '上次访问时间',
  `tstaus`         INT(11)              DEFAULT '0'
  COMMENT '0不置顶1置顶',
  `tzan`           BIGINT(20)           DEFAULT '0'
  COMMENT '点赞量',
  PRIMARY KEY (`tid`),
  KEY `tsid` (`tsid`),
  KEY `tuid` (`tuid`),
  KEY `ttopic` (`ttopic`),
  CONSTRAINT `topic_ibfk_1` FOREIGN KEY (`tsid`) REFERENCES `section` (`sid`)
    ON DELETE CASCADE,
  CONSTRAINT `topic_ibfk_2` FOREIGN KEY (`tuid`) REFERENCES `user` (`uid`)
    ON DELETE CASCADE
)
  ENGINE = InnoDB
  AUTO_INCREMENT = 16730
  DEFAULT CHARSET = utf8;

