DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `uid`        BIGINT(20)  NOT NULL AUTO_INCREMENT
  COMMENT '自增用户id',
  `uemail`     VARCHAR(20) NOT NULL
  COMMENT 'email账号',
  `upassword`  VARCHAR(30) NOT NULL
  COMMENT '密码',
  `unickname`  VARCHAR(20) NOT NULL
  COMMENT '昵称',
  `usex`       INT(11)              DEFAULT '0'
  COMMENT '性别',
  `ubirthday`  DATE                 DEFAULT NULL
  COMMENT '生日',
  `ulevel`     INT(11)              DEFAULT '1'
  COMMENT '等级',
  `headimg`    VARCHAR(30)          DEFAULT 'defaulthead.jpg'
  COMMENT '头像',
  `ustatement` VARCHAR(30)          DEFAULT '此人很懒，没什么个人说明'
  COMMENT '个人描述',
  `uregtime`   TIMESTAMP   NOT NULL DEFAULT CURRENT_TIMESTAMP
  COMMENT '注册时间',
  `ustate`     INT(11)              DEFAULT '0'
  COMMENT '被封状态',
  `upoint`     INT(11)              DEFAULT '0'
  COMMENT '积分',
  PRIMARY KEY (`uid`),
  UNIQUE KEY `uemail` (`uemail`, `unickname`)
)
  ENGINE = InnoDB
  AUTO_INCREMENT = 7
  DEFAULT CHARSET = utf8;

