DROP TRIGGER IF EXISTS `addTopicCommentCount`;
DELIMITER ;;
CREATE TRIGGER `addTopicCommentCount`
  AFTER INSERT
  ON `comment`
  FOR EACH ROW
  BEGIN
    UPDATE topic
    SET treplaycount = treplaycount + 1
    WHERE tid = new.ctid;
  END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `deleteTopicCommentCount`;
DELIMITER ;;
CREATE TRIGGER `deleteTopicCommentCount`
  AFTER DELETE
  ON `comment`
  FOR EACH ROW
  BEGIN
    UPDATE topic
    SET treplaycount = treplaycount - 1
    WHERE tid = old.ctid;
  END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `addStopicCount`;
DELIMITER ;;
CREATE TRIGGER `addStopicCount`
  AFTER INSERT
  ON `topic`
  FOR EACH ROW
  BEGIN
    UPDATE section
    SET stopiccount = stopiccount + 1
    WHERE sid = new.tsid;
  END
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `deleteStopicCount`;
DELIMITER ;;
CREATE TRIGGER `deleteStopicCount`
  AFTER DELETE
  ON `topic`
  FOR EACH ROW
  BEGIN
    UPDATE section
    SET stopiccount = stopiccount - 1
    WHERE sid = old.tsid;
  END
;;
DELIMITER ;
