CREATE TABLE sword.logtable
(
  lid  BIGINT(20) PRIMARY KEY              NOT NULL
  COMMENT '自增' AUTO_INCREMENT,
  uid  BIGINT(20)                          NOT NULL
  COMMENT '用户Id',
  ip   VARCHAR(32) COMMENT '用户ip',
  time TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
  COMMENT '操作时间',
  type INT(11)                             NOT NULL
  COMMENT '1登录2退出3修改密码4忘记密码再修改5发表文章6删除文章7发表评论8删除评论',
  CONSTRAINT logtable_ibfk_1 FOREIGN KEY (uid) REFERENCES user (uid)
    ON DELETE CASCADE
    ON UPDATE CASCADE
);
CREATE INDEX uid
  ON sword.logtable (uid);
