CREATE TABLE sword.topic
(
  tid            BIGINT(20) PRIMARY KEY              NOT NULL
  COMMENT '帖子自增id'          AUTO_INCREMENT,
  tsid           INT(11)                             NOT NULL
  COMMENT '所属板块',
  tuid           BIGINT(20)                          NOT NULL
  COMMENT '发帖人',
  ttopic         VARCHAR(50)                         NOT NULL
  COMMENT '主题',
  tcontent       LONGTEXT                            NOT NULL
  COMMENT '内容',
  ttime          TIMESTAMP DEFAULT CURRENT_TIMESTAMP NOT NULL
  COMMENT '发布时间',
  treplaycount   BIGINT(20) DEFAULT '0'
  COMMENT '回帖量',
  tclickcount    BIGINT(20) DEFAULT '0'
  COMMENT '点击量',
  tlastclicktime DATETIME COMMENT '上次访问时间',
  tstaus         INT(11)    DEFAULT '0'
  COMMENT '0不置顶1置顶',
  tzan           BIGINT(20) DEFAULT '0'
  COMMENT '点赞量',
  CONSTRAINT topic_ibfk_1 FOREIGN KEY (tsid) REFERENCES section (sid)
    ON DELETE CASCADE,
  CONSTRAINT topic_ibfk_2 FOREIGN KEY (tuid) REFERENCES user (uid)
    ON DELETE CASCADE
);
CREATE INDEX tsid
  ON sword.topic (tsid);
CREATE INDEX tuid
  ON sword.topic (tuid);
CREATE INDEX ttopic
  ON sword.topic (ttopic);
